jQuery(function() {
	initSlickCarousel();
	initTabs();
	initInViewport();
	initBgImage();
	initCounter();
	initRoundCarousel();
	initOpenClose();
	initLavalamp();
	initAnchors();
	initLightbox();
});

jQuery(window).on('load', function(){
	if (jQuery.isFunction(window.picturefill)) {
		picturefill();
	}
})


// lightbox init
function initLightbox() {
	jQuery('a.lightbox, a[data-rel*="lightbox"]').fancybox({
		helpers: {
			overlay: {
				css: {
					background: 'rgba(0, 0, 0, 0.65)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if (current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e) {
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		},
		wrapCSS: 'false',
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: 'a.anchor[href^="#"]:not([href="#"])',
		extraOffset: function() {
			var totalHeight = 0;
			jQuery('#header').each(function() {
				var $box = jQuery(this);
				var stickyInstance = $box.data('StickyScrollBlock');
				if (stickyInstance) {
					stickyInstance.stickyFlag = false;
					stickyInstance.stickyOn();
					totalHeight += $box.outerHeight();
					stickyInstance.onResize();
				} else {
					totalHeight += $box.css('position') === 'fixed' ? $box.outerHeight() - 2 : 0;
				}
			});
			return totalHeight;
		},
		wheelBehavior: 'none'
	});
}

// init lavalamp
function initLavalamp() {
	jQuery('.lavalamp').lavalamp({
		activeClass: 'active',
		item: '> li'
	});
}

// init round carousel
function initRoundCarousel() {
	var win = jQuery(window);

	jQuery('.rounded-slider').each(function() {
		var holder = jQuery(this);
		var gallery = holder.find('> .slider-wrap');
		var paginationItems = holder.find('.js-rounded-nav li');
		
		var textItemsContainer = holder.closest('.app-info-block').find('.js-fade-container');
		var textItems = textItemsContainer.find('.js-fade-item');
		
		var roundaboutAPI;

		gallery.roundabout({
			bearing: 240,
			minScale:0.6,
			maxScale: 1,
			childSelector: '.slide',
			autoplay:false,
			autoplayDuration: false,
			autoplayPauseOnHover: false,
			enableDrag: true,
			responsive: true,
			animateBearingToFocusCallback: function(angle, c, e, f) {
				if (!roundaboutAPI) return;
				var slideIndex = getSlideIndex(angle);
				refreshPagaintionClasses(slideIndex);
				refreshTextContent(slideIndex);
			}
		});
		roundaboutAPI = gallery.data('roundabout');
	
	if (roundaboutAPI) {
		refreshHeight();
		win.on('load resize orientationchange', refreshHeight);

		refreshPagaintionClasses(getSlideIndex(240));
		refreshTextContent(getSlideIndex(240), true);
		paginationItems.each(function(ind) {
			var item = jQuery(this);
			item.on('click', function(e) {
				e.preventDefault();
				gallery.find(item.data('rel')).trigger('click');
			});
		});
	}

	function refreshHeight(initial) {
		var maxHeight = 0;

		gallery.find('.slide').each(function(index) {
			var slideHeight = jQuery(this).outerHeight(true);

			if (slideHeight > maxHeight) {
				maxHeight = slideHeight;
			}
		});

		gallery.css({
			height: maxHeight
		});
		gallery.roundabout('relayoutChildren');
	}

	function getSlideIndex(angle) {
		if (!roundaboutAPI) return -1;

		var slideIndex = -1;
		gallery.find('.slide').each(function(index) {
			var slideAngle = jQuery(this).data('roundabout').degrees;
			if (slideAngle === angle) {
				slideIndex = index;
			}
		});
		return slideIndex;
	}

	function refreshPagaintionClasses(index) {
		paginationItems
			.removeClass('active')
			.eq(index)
				.addClass('active');
	}
	function refreshTextContent(index, noAnim) {
		var prevText = textItems.filter('.js-active-text').removeClass('js-active-text');
		var newText = textItems.eq(index).addClass('js-active-text');

		if (prevText.length) {
			textItemsContainer.css({ minHeight: textItemsContainer.innerHeight() });
			prevText.stop().fadeOut(noAnim ? 0 : 300, function() {
				newText.stop().fadeIn(noAnim ? 0 : 300);
				textItemsContainer.css({ minHeight: '', minWidth: '' });
			});
		} else {
			newText.stop().fadeIn(noAnim ? 0 : 300);
			win.trigger('resize');
		}
	}
	});
}

// open-close init
function initOpenClose() {
	jQuery('.open-close').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

// init counter
function initCounter() {
	var options = {
		useEasing : true,
		useGrouping : true,
		separator : ',', 
		decimal : '.', 
	};

	jQuery('.js-count').each(function() {
		var count = jQuery(this);
		var holder = count.closest('.viewport-section');
		var counter = new CountUp(count[0], 0, count.data('value'), 2, 2.5, options);

		holder.on('in-viewport', function() {
			counter.start();
		})
	});
}

// slick init
function initSlickCarousel() {
	jQuery('.slick-slider').slick({
		slidesToScroll: 1,
		prevArrow: '<button class="slick-prev">Previous</button>',
		nextArrow: '<button class="slick-next">Next</button>'
	});
}

// content tabs init
function initTabs() {
	jQuery('.network-tab').tabset({
		tabLinks: 'a',
		event: 'mouseenter',
		autoHeight: true,
		animSpeed: '1',
		addToParent: true,
		defaultTab: true
	});

	jQuery('.hover-tab').tabset({
		tabLinks: 'a',
		event: 'mouseenter',
		autoHeight: true,
		animSpeed: '1',
		addToParent: true,
		defaultTab: true
	});

	jQuery('.hover-tab a, .network-tab a').click(function(e) {
		e.preventDefault();
	});
}

